CREATE TABLE `civilstatus` (
  `CivilStatus_ID` int(11) NOT NULL,
  `Title` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`CivilStatus_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `clienttype` (
  `ClientType_ID` int(11) NOT NULL,
  `BusinessType` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`ClientType_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `deductions` (
  `Deduction_ID` int(11) NOT NULL,
  `DeductionType` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`Deduction_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `gender` (
  `Gender_ID` int(11) NOT NULL,
  `GenderName` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`Gender_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `grosstype` (
  `GrossType_ID` int(11) NOT NULL,
  `GrossTypeName` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`GrossType_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `paymenttype` (
  `PaymentType_ID` int(11) NOT NULL,
  `Type` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`PaymentType_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `status` (
  `Status_ID` int(11) NOT NULL AUTO_INCREMENT,
  `StatusName` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`Status_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
